// this는 함수 호출 방식에 따라 동적으로 결정 됨.
const foo = function() {
    console.log(this);
}

// 1. 일반 함수 호출
foo() // window


// 2. 메서드 호출
const obj = { foo };
obj.foo(); // obj

// 3. 생성자 함수 호출
new foo(); // foo {}

// 4. apply/call/bind 메서드에 의한 간접 호출
const bar = {name: 'bar'};
foo.call(bar); // bar
foo.apply(bar); //bar
foo.bind(bar)(); //bar

